import React, { Component } from 'react';

class MyComponent extends Component {
  constructor(props) {
    super(props);
    this.prismRef = React.createRef(); // Создаем ссылку на DOM-элемент для доступа к нему из методов класса
  }

  componentDidMount() {
    // Вызывается после монтирования компонента
    this.prism = this.prismRef.current; // Получаем DOM-элемент из ссылки
    // Подключаем обработчики событий
    this.prism.querySelector(".signup").addEventListener("click", this.showSignup);
    this.prism.querySelector(".login").addEventListener("click", this.showLogin);
    this.prism.querySelector(".forgot-password").addEventListener("click", this.showForgotPassword);
    this.prism.querySelector(".subscribe").addEventListener("click", this.showSubscribe);
    this.prism.querySelector(".contact-us").addEventListener("click", this.showContactUs);
    this.prism.querySelector(".thank-you").addEventListener("click", this.showThankYou);
  }

  componentWillUnmount() {
    // Вызывается перед удалением компонента
    // Отключаем обработчики событий
    this.prism.querySelector(".signup").removeEventListener("click", this.showSignup);
    this.prism.querySelector(".login").removeEventListener("click", this.showLogin);
    this.prism.querySelector(".forgot-password").removeEventListener("click", this.showForgotPassword);
    this.prism.querySelector(".subscribe").removeEventListener("click", this.showSubscribe);
    this.prism.querySelector(".contact-us").removeEventListener("click", this.showContactUs);
    this.prism.querySelector(".thank-you").removeEventListener("click", this.showThankYou);
  }

  showSignup = () => {
    this.prism.style.transform = "translateZ(-100px) rotateY( -90deg)";
  }

  showLogin = () => {
    this.prism.style.transform = "translateZ(-100px)";
  }

  showForgotPassword = () => {
    this.prism.style.transform = "translateZ(-100px) rotateY( -180deg)";
  }

  showSubscribe = () => {
    this.prism.style.transform = "translateZ(-100px) rotateX( -90deg)";
  }

  showContactUs = () => {
    this.prism.style.transform = "translateZ(-100px) rotateY( 90deg)";
  }

  showThankYou = () => {
    this.prism.style.transform = "translateZ(-100px) rotateX( 90deg)";
  }

  render() {
    return (
      <div className="rec-prism" ref={this.prismRef}>
        {/* Дочерние элементы */}
        ХУЙ
      </div>
    );
  }
}

export default MyComponent;
